    {
      var ctx = wx.createCanvasContext('firstCanvas');
      // 右边白色的半圆
      ctx.fillStyle = "#fff";
      ctx.beginPath();
      ctx.arc(100, 100, 100, (Math.PI / 180) * 270, (Math.PI / 180) * 90);
      ctx.fill();

      // 左边黑色的圆
      ctx.fillStyle = "#000";
      ctx.beginPath();
      ctx.arc(100, 100, 100, (Math.PI / 180) * 270, (Math.PI / 180) * 90, true);
      ctx.fill();
      // 左边白色的小圆
      ctx.fillStyle = "#fff";
      ctx.beginPath();
      ctx.arc(100, 50, 50, (Math.PI / 180) * 270, (Math.PI / 180) * 90, true);
      ctx.fill();

      // 右边黑色的小圆
      ctx.fillStyle = "#000";
      ctx.beginPath();
      ctx.arc(100, 150, 50, (Math.PI / 180) * 270, (Math.PI / 180) * 90);
      ctx.fill();

      // 黑色的小圆点
      ctx.fillStyle = "#000";
      ctx.beginPath();
      ctx.arc(100, 50, 15, 0, Math.PI * 2);
      ctx.fill();

      // 白色的小圆点
      ctx.fillStyle = "#fff";
      ctx.beginPath();
      ctx.arc(100, 150, 15, 0, Math.PI * 2);
      ctx.fill();
      ctx.draw();

    }

